// src/pages/auth/LoginPage.js

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../../api/auth';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post('auth/token/login/', { email, password });
      
      // Check if the login was successful
      if (response.status === 200 && response.data.auth_token) {
        // Store the token and a placeholder role
        localStorage.setItem('token', response.data.auth_token);
        
        // This is a placeholder for checking the user's role.
        // In a real app, the token or a separate API call would tell you the role.
        if (email.endsWith('@admin.com')) { 
          localStorage.setItem('role', 'admin');
          navigate('/admin/dashboard');
        } else {
          localStorage.setItem('role', 'employee');
          navigate('/employee/dashboard');
        }
      } else {
        setError('Login failed. Please try again.');
      }
    } catch (err) {
      console.error('Login error:', err);
      setError('Invalid email or password. Please check your credentials.');
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Log In</h2>
      <form onSubmit={handleLogin} style={{ display: 'flex', flexDirection: 'column', gap: '10px', maxWidth: '300px' }}>
        <input 
          type="email" 
          placeholder="Email" 
          value={email} 
          onChange={(e) => setEmail(e.target.value)} 
          required 
        />
        <input 
          type="password" 
          placeholder="Password" 
          value={password} 
          onChange={(e) => setPassword(e.target.value)} 
          required 
        />
        <button type="submit">Login</button>
        {error && <p style={{ color: 'red', marginTop: '10px' }}>{error}</p>}
      </form>
    </div>
  );
};

export default LoginPage;
