// src/pages/auth/LoginPage.js

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { login } from '../../api/auth';
import InputField from '../../components/common/InputField';
import Button from '../../components/common/Button';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    try {
      const response = await login(email, password);
												 
      
      if (response && response.auth_token) {
        localStorage.setItem('token', response.auth_token);
        
        // This is a placeholder for checking the user's role.
        // In a real app, the token or a separate API call would tell you the role.
        if (email.endsWith('@admin.com')) { 
          localStorage.setItem('role', 'admin');
          navigate('/admin/dashboard');
        } else {
          localStorage.setItem('role', 'employee');
          navigate('/employee/dashboard');
        }
      } else {
        setError('Login failed. Please try again.');
      }
    } catch (err) {
      console.error('Login error:', err);
      setError('Invalid email or password. Please check your credentials.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container page" style={{ marginTop: '50px', maxWidth: '400px' }}>
<p className="welcome-message">Welcome to your payslip dashboard, login to view your payslip</p>
     								 
								 
											
																										
      <h2>Log In</h2>
      <form onSubmit={handleLogin}>
        <InputField 
          label="Email" 
				  
          type="email" 
						
          value={email} 
          onChange={(e) => setEmail(e.target.value)} 
          required 
        />
        <InputField 
									   
          label="Password" 
				  
          type="password" 
						   
          value={password} 
          onChange={(e) => setPassword(e.target.value)} 
          required 
        />
        <Button type="submit" disabled={loading}>{loading ? 'Logging in...' : 'Login'}</Button>
																					   
			   
        {error && <p className="error" style={{ marginTop: '10px' }}>{error}</p>}
      </form>
    </div>
  );
};

export default LoginPage;
