// src/pages/employee/MyPayslipsPage.js

import React, { useEffect, useState } from 'react';
import api from '../../api/auth'; // Using the same axios instance with auth headers
import { useNavigate } from 'react-router-dom';

const MyPayslipsPage = () => {
  const [payslips, setPayslips] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchPayslips = async () => {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/login');
        return;
      }
      try {
        const response = await api.get('payslips/', {
          headers: {
            Authorization: `Token ${token}`,
          },
        });
        setPayslips(response.data);
      } catch (error) {
        console.error('Failed to fetch payslips:', error);
        // Handle token expiration or invalid token
        if (error.response && error.response.status === 401) {
          localStorage.removeItem('token');
          localStorage.removeItem('role');
          navigate('/login');
        }
      } finally {
        setLoading(false);
      }
    };

    fetchPayslips();
  }, [navigate]);

  if (loading) {
    return <div style={{ padding: '20px' }}>Loading payslips...</div>;
  }

  return (
    <div style={{ padding: '20px' }}>
      <h2 style={{ marginBottom: '20px' }}>My Payslips</h2>
      {payslips.length > 0 ? (
        payslips.map(payslip => (
          <div key={payslip.id} style={{ border: '1px solid #ccc', padding: '15px', marginBottom: '15px', borderRadius: '8px' }}>
            <h3>Payslip for {payslip.employee_name}</h3>
            <p><strong>Period:</strong> {payslip.payroll_run.period_start_date} to {payslip.payroll_run.period_end_date}</p>
            <p><strong>Net Pay:</strong> KES {payslip.net_pay}</p>
          </div>
        ))
      ) : (
        <p>No payslips found.</p>
      )}
    </div>
  );
};

export default MyPayslipsPage;
