// Debug component to check navigation state
import React from 'react';

const NavigationDebug = () => {
  const userRole = localStorage.getItem('role');
  const token = localStorage.getItem('token');
  
  return (
    <div style={{ 
      position: 'fixed', 
      top: '10px', 
      right: '10px', 
      background: 'rgba(0,0,0,0.8)', 
      color: 'white', 
      padding: '10px',
      fontSize: '12px',
      zIndex: 9999,
      borderRadius: '5px'
    }}>
      <div>Role: {userRole || 'No role'}</div>
      <div>Token: {token ? `${token.substring(0,10)}...` : 'No token'}</div>
      <div>Nav should show: {userRole === 'employee' ? 'Employee Menu' : userRole === 'admin' ? 'Admin Menu' : 'No Menu'}</div>
    </div>
  );
};

export default NavigationDebug;
