// src/pages/admin/DashboardPage.js

import React, { useEffect, useState } from 'react';
import api from '../../api/auth';
import { useNavigate } from 'react-router-dom';

const AdminDashboardPage = () => {
  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchEmployees = async () => {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/login');
        return;
      }
      try {
        const response = await api.get('employees/', {
          headers: {
            Authorization: `Token ${token}`,
          },
        });
        setEmployees(response.data);
      } catch (error) {
        console.error('Failed to fetch employees:', error);
        if (error.response && error.response.status === 401) {
          localStorage.removeItem('token');
          localStorage.removeItem('role');
          navigate('/login');
        }
      } finally {
        setLoading(false);
      }
    };

    fetchEmployees();
  }, [navigate]);

  if (loading) {
    return <div style={{ padding: '20px' }}>Loading employees...</div>;
  }

  return (
    <div style={{ padding: '20px' }}>
      <h2 style={{ marginBottom: '20px' }}>Admin Dashboard</h2>
      <h3>All Employees</h3>
      {employees.length > 0 ? (
        <ul style={{ listStyleType: 'none', padding: 0 }}>
          {employees.map(employee => (
            <li key={employee.id} style={{ border: '1px solid #ccc', padding: '10px', marginBottom: '10px', borderRadius: '4px' }}>
              <p><strong>Name:</strong> {employee.full_name}</p>
              <p><strong>Email:</strong> {employee.user.email}</p>
            </li>
          ))}
        </ul>
      ) : (
        <p>No employees found.</p>
      )}
    </div>
  );
};

export default AdminDashboardPage;
