import axios from 'axios';

// The base URL for your Django backend
const API_URL = 'http://127.0.0.1:8000/api/v1/';

const api = axios.create({
  baseURL: API_URL,
});

export const login = async (email, password) => {
  const response = await api.post('auth/token/login/', { email, password });
  return response.data; // This should contain the auth token
};

// You can add other auth-related functions here, like register or logout

export default api;
