// src/pages/employee/LeaveRequestsPage.js

import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import InputField from '../../components/common/InputField';
import Button from '../../components/common/Button';
import { 
  getLeaveTypes, 
  getLeaveRequests, 
  createLeaveRequest, 
  getMyLeaveBalance 
} from '../../api/leaves';

const LeaveRequestsPage = () => {
  const [leaveRequests, setLeaveRequests] = useState([]);
  const [leaveTypes, setLeaveTypes] = useState([]);
  const [leaveBalances, setLeaveBalances] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    leave_type: '',
    start_date: '',
    end_date: '',
    reason: '',
    days: 0
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const navigate = useNavigate();

  // Load data from API
  useEffect(() => {
    loadLeaveData();
  }, []);

  const loadLeaveData = async () => {
    try {
      setLoading(true);
      const [typesResponse, requestsResponse, balancesResponse] = await Promise.all([
        getLeaveTypes(),
        getLeaveRequests(),
        getMyLeaveBalance()
      ]);

      setLeaveTypes(typesResponse.results || typesResponse);
      setLeaveRequests(requestsResponse.results || requestsResponse);
      setLeaveBalances(balancesResponse.results || balancesResponse);
      setError(null);
    } catch (err) {
      console.error('Failed to load leave data:', err);
      setError('Failed to load leave data. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const calculateDays = (start, end) => {
    if (!start || !end) return 0;
    const startDate = new Date(start);
    const endDate = new Date(end);
    const diffTime = Math.abs(endDate - startDate);
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24)) + 1; // Include both start and end days
    return diffDays;
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    const newFormData = { ...formData, [name]: value };
    
    // Auto-calculate days when dates change
    if (name === 'startDate' || name === 'endDate') {
      newFormData.days = calculateDays(
        name === 'startDate' ? value : formData.startDate,
        name === 'endDate' ? value : formData.endDate
      );
    }
    
    setFormData(newFormData);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setSuccess(null);

    try {
      // Mock API call - in real app, this would submit to backend
      await new Promise(resolve => setTimeout(resolve, 1000)); // Simulate API delay

      // Add new request to list (mock)
      const newRequest = {
        id: leaveRequests.length + 1,
        leaveType: formData.leaveType === 'annual' ? 'Annual Leave' : 
                   formData.leaveType === 'sick' ? 'Sick Leave' : 
                   formData.leaveType === 'personal' ? 'Personal Leave' : 'Other',
        startDate: formData.startDate,
        endDate: formData.endDate,
        days: formData.days,
        reason: formData.reason,
        status: 'Pending',
        appliedDate: new Date().toISOString().split('T')[0],
        approvedBy: null
      };

      setLeaveRequests([newRequest, ...leaveRequests]);
      setSuccess('Leave request submitted successfully!');
      setFormData({
        leaveType: 'annual',
        startDate: '',
        endDate: '',
        reason: '',
        days: 0
      });
      setShowForm(false);
    } catch (err) {
      console.error('Failed to submit leave request:', err);
      setError('Failed to submit leave request. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'Approved': return 'var(--success-color)';
      case 'Rejected': return 'var(--danger-color)';
      case 'Pending': return 'var(--warning-color)';
      default: return 'var(--secondary-color)';
    }
  };

  return (
    <div className="container page">
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px' }}>
        <h2>Leave Requests</h2>
        <Button 
          onClick={() => setShowForm(!showForm)}
          className="btn-primary"
        >
          {showForm ? 'Cancel' : 'New Leave Request'}
        </Button>
      </div>

      {success && <div className="alert alert-success">{success}</div>}
      {error && <div className="alert alert-error">{error}</div>}

      {/* Leave Request Form */}
      {showForm && (
        <div className="card" style={{ marginBottom: '30px' }}>
          <h3>Submit New Leave Request</h3>
          <form onSubmit={handleSubmit}>
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '20px' }}>
              <div className="form-group">
                <label htmlFor="leaveType">Leave Type</label>
                <select
                  id="leaveType"
                  name="leaveType"
                  value={formData.leaveType}
                  onChange={handleInputChange}
                  required
                >
                  <option value="annual">Annual Leave</option>
                  <option value="sick">Sick Leave</option>
                  <option value="personal">Personal Leave</option>
                  <option value="emergency">Emergency Leave</option>
                  <option value="maternity">Maternity/Paternity Leave</option>
                </select>
              </div>

              <InputField
                label="Start Date"
                type="date"
                name="startDate"
                value={formData.startDate}
                onChange={handleInputChange}
                required
              />

              <InputField
                label="End Date"
                type="date"
                name="endDate"
                value={formData.endDate}
                onChange={handleInputChange}
                required
              />

              <InputField
                label="Total Days"
                type="number"
                name="days"
                value={formData.days}
                readOnly
                style={{ backgroundColor: '#f8f9fa' }}
              />
            </div>

            <div className="form-group" style={{ marginTop: '20px' }}>
              <label htmlFor="reason">Reason for Leave</label>
              <textarea
                id="reason"
                name="reason"
                value={formData.reason}
                onChange={handleInputChange}
                rows="3"
                style={{ width: '100%', padding: '10px', border: '1px solid var(--border-color)', borderRadius: '5px' }}
                placeholder="Please provide a brief reason for your leave request..."
                required
              />
            </div>

            <div style={{ marginTop: '20px' }}>
              <Button type="submit" disabled={loading} className="btn-primary">
                {loading ? 'Submitting...' : 'Submit Request'}
              </Button>
            </div>
          </form>
        </div>
      )}

      {/* Leave Requests History */}
      <div className="card">
        <h3>Leave Request History</h3>
        {leaveRequests.length > 0 ? (
          <div style={{ overflowX: 'auto' }}>
            <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '20px' }}>
              <thead>
                <tr style={{ backgroundColor: 'var(--light-color)' }}>
                  <th style={{ padding: '12px', textAlign: 'left', border: '1px solid var(--border-color)' }}>Type</th>
                  <th style={{ padding: '12px', textAlign: 'left', border: '1px solid var(--border-color)' }}>Start Date</th>
                  <th style={{ padding: '12px', textAlign: 'left', border: '1px solid var(--border-color)' }}>End Date</th>
                  <th style={{ padding: '12px', textAlign: 'left', border: '1px solid var(--border-color)' }}>Days</th>
                  <th style={{ padding: '12px', textAlign: 'left', border: '1px solid var(--border-color)' }}>Status</th>
                  <th style={{ padding: '12px', textAlign: 'left', border: '1px solid var(--border-color)' }}>Applied Date</th>
                  <th style={{ padding: '12px', textAlign: 'left', border: '1px solid var(--border-color)' }}>Reason</th>
                </tr>
              </thead>
              <tbody>
                {leaveRequests.map((request) => (
                  <tr key={request.id}>
                    <td style={{ padding: '12px', border: '1px solid var(--border-color)' }}>{request.leaveType}</td>
                    <td style={{ padding: '12px', border: '1px solid var(--border-color)' }}>{request.startDate}</td>
                    <td style={{ padding: '12px', border: '1px solid var(--border-color)' }}>{request.endDate}</td>
                    <td style={{ padding: '12px', border: '1px solid var(--border-color)' }}>{request.days}</td>
                    <td style={{ 
                      padding: '12px', 
                      border: '1px solid var(--border-color)',
                      color: getStatusColor(request.status),
                      fontWeight: 'bold'
                    }}>
                      {request.status}
                    </td>
                    <td style={{ padding: '12px', border: '1px solid var(--border-color)' }}>{request.appliedDate}</td>
                    <td style={{ padding: '12px', border: '1px solid var(--border-color)' }}>{request.reason}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : (
          <p>No leave requests found.</p>
        )}
      </div>
    </div>
  );
};

export default LeaveRequestsPage;
