// src/App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, Outlet } from 'react-router-dom';
import LoginPage from './pages/auth/LoginPage';
import MyPayslipsPage from './pages/employee/MyPayslipsPage';
import DashboardPage from './pages/admin/DashboardPage';
import EmployeeManagementPage from './pages/admin/EmployeeManagementPage';
import PayrollRunPage from './pages/admin/PayrollRunPage';
import MyProfilePage from './pages/employee/MyProfilePage';
import EmployeePortal from './pages/employee/EmployeePortal'; // Import the new component
import { login, logout } from './api/auth';

// A simple layout for pages that are not the login screen
const AppLayout = () => {
  const userRole = localStorage.getItem('role');

  return (
    <div>
      <nav className="navbar">
        <h2>Payroll System</h2>
        <div>
          {userRole === 'employee' && (
            <>
              <a href="/employee/dashboard">My Dashboard</a>
              <a href="/employee/profile">My Profile</a>
            </>
          )}
          {userRole === 'admin' && (
            <>
              <a href="/admin/dashboard">Dashboard</a>
              <a href="/admin/employees">Employees</a>
              <a href="/admin/payroll-run">Run Payroll</a>
            </>
          )}
          <a href="#" onClick={logout}>Logout</a>
        </div>
      </nav>
      <div className="container">
        <Outlet />
      </div>
    </div>
  );
};

// Simple check for authentication and role-based access
const isAuthenticated = () => !!localStorage.getItem('token');
const getUserRole = () => localStorage.getItem('role');

// A route that requires authentication
const PrivateRoute = ({ children, roleRequired }) => {
  const auth = isAuthenticated();
  const userRole = getUserRole();
  
  if (!auth) {
    return <Navigate to="/login" replace />;
  }

  if (roleRequired && userRole !== roleRequired) {
    return <Navigate to={userRole === 'admin' ? '/admin/dashboard' : '/employee/dashboard'} replace />;
  }

  return children;
};

const App = () => {
  return (
    <Router>
      <Routes>
        <Route path="/login" element={<LoginPage />} />
        
        <Route element={<AppLayout />}>
          {/* Employee Protected Routes */}
          <Route
            path="/employee/dashboard"
            element={
              <PrivateRoute roleRequired="employee">
                <EmployeePortal />
              </PrivateRoute>
            }
          />
          <Route
            path="/employee/dashboard/payslips"
            element={
              <PrivateRoute roleRequired="employee">
                <MyPayslipsPage />
              </PrivateRoute>
            }
          />
          <Route
            path="/employee/dashboard/profile"
            element={
              <PrivateRoute roleRequired="employee">
                <MyProfilePage />
              </PrivateRoute>
            }
          />
          
          {/* Admin Protected Routes */}
          <Route
            path="/admin/dashboard"
            element={
              <PrivateRoute roleRequired="admin">
                <DashboardPage />
              </PrivateRoute>
            }
          />
          <Route
            path="/admin/employees"
            element={
              <PrivateRoute roleRequired="admin">
                <EmployeeManagementPage />
              </PrivateRoute>
            }
          />
          <Route
            path="/admin/payroll-run"
            element={
              <PrivateRoute roleRequired="admin">
                <PayrollRunPage />
              </PrivateRoute>
            }
          />

          {/* Redirect to the appropriate dashboard if already logged in */}
          <Route path="/" element={<Navigate to={isAuthenticated() ? `/${getUserRole()}/dashboard` : '/login'} replace />} />
          
          {/* Fallback for unknown routes */}
          <Route path="*" element={<Navigate to="/" replace />} />
        </Route>
      </Routes>
    </Router>
  );
};

export default App;
